package com.haoyayi.thor.utils;

import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;

import org.apache.commons.lang3.StringUtils;

import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

/**
 * @author dujingwei
 * @date 2015年11月21日
 */
public class Base64Utils {

	public static void main(String[] args) {
		// 测试从Base64编码转换为图片文件

//		String strImg = getImageStr("/Users/dujingwei/Downloads/COCPhoto/coc_4_3_ben_9.png");
//		String strImg = "";
//		System.out.println(strImg);
//		generateImage(strImg, "/Users/dujingwei/Downloads/COCPhoto/4");

		String a[] = getFileExt("data:image/jpeg;base64,");
		System.out.println(a[0]);
		System.out.println(a[1]);
		generateImage(a[1], "/Users/dujingwei/Downloads/COCPhoto/5"+"."+a[0]);
	}

	/**
	 * 将图片文件转化为字节数组字符串，并对其进行Base64编码处理
	 * @param imgFilePath
	 * @return
	 */
	public static String getImageStr(String imgFilePath) {
		byte[] data = null;

		// 读取图片字节数组
		InputStream in = null;
		try {
			in = new FileInputStream(imgFilePath);
			data = new byte[in.available()];
			in.read(data);
			in.close();
		} catch (IOException e) {
			e.printStackTrace();
		} finally {
			if(in != null) {
				try {
					in.close();
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
		}

		// 对字节数组Base64编码
		BASE64Encoder encoder = new BASE64Encoder();
		return encoder.encode(data);// 返回Base64编码过的字节数组字符串
	}
	
	/**
	 * 把base64编码的文件后缀拿过来
	 * data:image/jpeg;base64,xxxx
	 * @param base64
	 * @return
	 */
	public  static String[] getFileExt(String base64) {
		if(StringUtils.isNotBlank(base64)) {
			String[] data = base64.split("base64,");
			String ext = data[0].split("/")[1].replaceAll(";", "");
			String content = data[1];
			return new String[]{ext, content};
		}
		return null;
	}

	public static boolean generateImage(String imgStr, String imgFilePath) {// 对字节数组字符串进行Base64解码并生成图片
		if (imgStr == null) // 图像数据为空
			return false;
		BASE64Decoder decoder = new BASE64Decoder();
		OutputStream out = null;
		try {
			// Base64解码
			byte[] bytes = decoder.decodeBuffer(imgStr);
			for (int i = 0; i < bytes.length; ++i) {
				if (bytes[i] < 0) {// 调整异常数据
					bytes[i] += 256;
				}
			}
			// 生成jpeg图片
			out = new FileOutputStream(imgFilePath);
			out.write(bytes);
			out.flush();
			return true;
		} catch (Exception e) {
			return false;
		} finally {
			if(out != null) {
				try {
					out.close();
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
		}
	}
}
